    private void bHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bHitungActionPerformed
        if(tfNama.getText().equals("")||
            tfBB.getText().equals("")||
            tfTinggi.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Kotak isian harus diisi semua");
        } else if(jRadioButton1.isSelected()||jRadioButton2.isSelected()) {
            try {
                Proses();
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "Masukan ada yang salah");
            }
        } else {
            JOptionPane.showMessageDialog(null, "Anda harus memilih jenis kelamin");
        }
    }//GEN-LAST:event_bHitungActionPerformed

