    private void Proses() {
        h = Double.valueOf(tfTinggi.getText());
        w = Double.valueOf(tfBB.getText());
        
        if (jRadioButton1.isSelected()) {
            Res = (h - 104) * 1;
        } else if (jRadioButton2.isSelected()) {
            Res = (h - 100) * 1;
        }
        temp = Double.toString(Res.intValue());
        
        if (Res < w) {
            tfRes.setText(temp);
            tfD1.setText("Maaf "+tfNama.getText()+", Sepertinya anda Overweight");
            tfD2.setText("Banyaklah berolahraga dan hindari makanan berkolestrol");
        } else if (Res > w) {
            tfRes.setText(temp);
            tfD1.setText("Maaf "+tfNama.getText()+", Sepertinya anda Underweight");
            tfD2.setText("Banyaklah mengkonsumsi makanan yang berkarbohidrat");
        } else {
            tfRes.setText(temp);
            tfD1.setText("Hei "+tfNama.getText()+", Berat badan anda sudah ideal");
            tfD2.setText("Tetap jaga pola makan teratur dan gaya hidup sehat");
        }
    }
